import React, { useEffect, useState } from 'react';
import UilSearch from '@iconscout/react-unicons/icons/uil-search';
import UilToggle from '@iconscout/react-unicons/icons/uil-arrow-left';
import UilSetting from '@iconscout/react-unicons/icons/uil-setting';
import UilNotification from '@iconscout/react-unicons/icons/uil-bell';
import UilAddon from '@iconscout/react-unicons/icons/uil-plus';
import UilBoard from '@iconscout/react-unicons/icons/uil-layer-group';
import UilUser from '@iconscout/react-unicons/icons/uil-user';
import UilStatus from '@iconscout/react-unicons/icons/uil-clock';

import { useDarkMode } from './DarkModeContext';
import datas from '../data.json';
import { Tab, Tabs, Dropdown } from 'react-bootstrap';
import userAvatar from '../images/avatar.png';

const Tasks = ({ handleSidebarToggle }) => {
    const [key, setKey] = useState('tasks');
    const { darkMode } = useDarkMode();
    const [tasks, setTasks] = useState([]);

    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const flattenedTasks = datas.flatMap(sprint =>
            sprint.milestones.flatMap(milestone =>
                milestone.tasks.map(task => ({ ...task, milestoneName: milestone.milestoneName }))
            )
        );
        setTasks(flattenedTasks);
    }, []);

    const filterTasks = tasks => {
        return tasks.filter(task => task.name.toLowerCase().includes(searchTerm.toLowerCase()));
    };

    return (
        <>
            <section className={`dashboard ${darkMode ? 'dark-mode' : 'light-mode'}`}>
                <div className="top">
                    <UilToggle onClick={handleSidebarToggle} className='sidebar-toggle' />
                    <div className='project-head'>
                        <div className='project-title'>
                            <h3>Basic Structure Kanban Project</h3>
                            <ul>
                                <li><a href='#' className='hash-tag'>#BSKP</a></li>
                                <li><p className='settings'><UilSetting /></p></li>
                                <li><p className='notify'><UilNotification /> </p></li>
                            </ul>
                        </div>

                        <div className='project-slogan'>
                            <p>Paymo SRL</p>
                        </div>
                    </div>
                </div>

                <Tabs
                    id="controlled-tab-example"
                    activeKey={key}
                    onSelect={(k) => setKey(k)}
                >
                    <Tab eventKey="overview" title="Overview" disabled></Tab>

                    <Tab eventKey="tasks" title="Tasks">
                        <div className='task-nav pt-3'>
                            <div className='task-filter'>
                                <ul>
                                    <li>
                                        <input
                                            type="text"
                                            placeholder="Search tasks by name"
                                            value={searchTerm}
                                            onChange={(e) => setSearchTerm(e.target.value)}
                                            className='search-input'
                                        />
                                    </li>
                                    <li>
                                        <Dropdown>
                                            <Dropdown.Toggle variant="" id="dropdown-basic">
                                                <UilBoard /> Board
                                            </Dropdown.Toggle>

                                            <Dropdown.Menu>
                                                <Dropdown.Item href="#">Action</Dropdown.Item>
                                                <Dropdown.Item href="#">Another action</Dropdown.Item>
                                                <Dropdown.Item href="#">Something else</Dropdown.Item>
                                            </Dropdown.Menu>
                                        </Dropdown>
                                    </li>
                                </ul>
                            </div>
                        </div>

                        <div className='task-layouts container pt-3'>
                            <div className='row'>
                                <div className='col-lg-12 col-md-12 col-sm-12'>
                                    <p className='layout-title b-primary'>RECENT ISSUES <span>({filterTasks(tasks).length})</span></p>
                                    {Array.isArray(tasks) && tasks.length > 0 ? (
                                        filterTasks(tasks).map((task, index) => (
                                            index % 3 === 0 ? (
                                                <div className='row' key={`row-${index}`}>
                                                    {[0, 1, 2].map(offset => {
                                                        const currentTask = tasks[index + offset];
                                                        return currentTask ? (
                                                            <div className='col-md-4' key={currentTask.primaryId}>
                                                                <div className='task-board'>
                                                                    <p className='tasktitle'>{currentTask.name}</p>
                                                                    <a href='#' className='task-tag'>
                                                                        <UilUser /> {currentTask.assignee}
                                                                    </a>
                                                                    <a href='#' className={`task-tag priority ${(currentTask.priority)}`}>
                                                                        <UilStatus /> {currentTask.priority}
                                                                    </a>
                                                                    <p className='pt-2'>
                                                                        <img src={userAvatar} alt='' />
                                                                        <span className='datetime'>{currentTask.startDate}</span>
                                                                        <span className='datetime'>Effect <strong>{currentTask.effortSpent}</strong></span>
                                                                    </p>
                                                                </div>
                                                            </div>
                                                        ) : null;
                                                    })}
                                                </div>
                                            ) : null
                                        ))
                                    ) : (
                                        <p>No tasks available.</p>
                                    )}
                                </div>
                            </div>
                        </div>
                    </Tab>

                    <Tab eventKey="milstones" title="Milstones" disabled></Tab>
                    <Tab eventKey="timesheets" title="Timesheets" disabled></Tab>
                    <Tab eventKey="files" title="Files" disabled></Tab>
                    <Tab eventKey="discussions" title="Discussions" disabled></Tab>
                    <Tab eventKey="activityfeed" title="Activity Feed" disabled>
                        <p>Content for activity feed tab</p>
                    </Tab>
                </Tabs>
            </section>
        </>
    );
};

export default Tasks;
